import chai from 'chai';
import chaiHttp from 'chai-http';
import app from '../controllers/appointmentController'; // Update the path accordingly
chai.use(chaiHttp);

const { expect } = chai;

describe('Appointment Controller', () => {
    describe('GET /api/appointments', () => {
        it('should get all appointments', (done) => {
            chai.request(app)
                .get('/api/appointments')
                .end((err, res) => {
                    expect(res).to.have.status(200);
                    expect(res.body).to.be.an('array');
                    done();
                });
        });
    });

    describe('POST /api/appointments/byUserName', () => {
        it('should get appointments by user name', (done) => {
            chai.request(app)
                .post('/api/appointments/byUserName')
                .send({ userName: 'exampleUser' }) // replace with valid user name
                .end((err, res) => {
                    expect(res).to.have.status(200);
                    expect(res.body).to.be.an('array');
                    done();
                });
        });
    });

    describe('POST /api/appointments', () => {
        it('should create a new appointment', (done) => {
            chai.request(app)
                .post('/api/appointments')
                .send({
                    // provide appointment data for creation
                })
                .end((err, res) => {
                    expect(res).to.have.status(200);
                    expect(res.text).to.equal('New appointment created successfully');
                    done();
                });
        });

        it('should handle errors when creating a new appointment', (done) => {
            chai.request(app)
                .post('/api/appointments')
                .send({
                    // provide invalid appointment data for creation
                })
                .end((err, res) => {
                    expect(res).to.have.status(500);
                    expect(res.text).to.equal('Internal server error');
                    done();
                });
        });
    });

    describe('GET /api/appointments/:id', () => {
        it('should get an appointment by ID', (done) => {
            chai.request(app)
                .get('/api/appointments/1') // replace with a valid appointment ID
                .end((err, res) => {
                    expect(res).to.have.status(200);
                    expect(res.body).to.be.an('object');
                    done();
                });
        });

        it('should handle errors when getting an appointment by invalid ID', (done) => {
            chai.request(app)
                .get('/api/appointments/invalidId')
                .end((err, res) => {
                    expect(res).to.have.status(500);
                    expect(res.text).to.equal('Internal server error');
                    done();
                });
        });

        it('should handle not found when getting an appointment by non-existent ID', (done) => {
            chai.request(app)
                .get('/api/appointments/nonExistentId')
                .end((err, res) => {
                    expect(res).to.have.status(404);
                    expect(res.text).to.equal('Appointment not found');
                    done();
                });
        });
    });

    // Add more test cases for other CRUD operations

    // Example: testing appointment update
    describe('PUT /api/appointments/:id', () => {
        it('should update an appointment by ID', (done) => {
            chai.request(app)
                .put('/api/appointments/1') // replace with a valid appointment ID
                .send({
                    // provide updated appointment data
                })
                .end((err, res) => {
                    expect(res).to.have.status(200);
                    expect(res.text).to.equal('Updated');
                    done();
                });
        });

        it('should handle errors when updating an appointment with invalid data', (done) => {
            chai.request(app)
                .put('/api/appointments/1') // replace with a valid appointment ID
                .send({
                    // provide invalid updated appointment data
                })
                .end((err, res) => {
                    expect(res).to.have.status(500);
                    expect(res.text).to.equal('Internal server error');
                    done();
                });
        });

        it('should handle not found when updating a non-existent appointment', (done) => {
            chai.request(app)
                .put('/api/appointments/nonExistentId')
                .send({
                    // provide updated appointment data
                })
                .end((err, res) => {
                    expect(res).to.have.status(404);
                    expect(res.text).to.equal('Appointment not found');
                    done();
                });
        });
    });

    // Add more test cases for other endpoints

});
